// import React from 'react'
// import Header from '../../composants/Header'
// import HeaderContent from '../../composants/HeaderContent'
// import Footer from '../../composants/Footer'
// import './QuestionCSS.css';

// const QuestionReponseChrono = () => {
//     return (
//         <>
//             <Header />
//             <HeaderContent />
//             <div className='containeur'>
//                 <h1 className='containeur'>TEST D'EVALUATION DES LEADERS</h1>
//                 <h3 className=''>Repondre aux questions pour valider l'examain</h3>
//             </div>

//             <Footer />
//         </>
//     )
// }

// export default QuestionReponseChrono



import React, { useState, useEffect, useRef } from 'react';

const QuestionPage = () => {
    const [questions, setQuestions] = useState([
        {
            texte: "Quelle est la capitale de la France ?",
            description: "Choisissez la bonne réponse parmi les options suivantes."
        },
        {
            texte: "Combien de continents y a-t-il sur Terre ?",
            description: "Choisissez la bonne réponse parmi les options suivantes."
        },
        // ... autres questions
    ]);
    const [answers, setAnswers] = useState([]);
    const [remainingTime, setRemainingTime] = useState(60); // 60 secondes par défaut
    const [isTimerActive, setIsTimerActive] = useState(false);
    const questionRef = useRef(null);

    useEffect(() => {
        const interval = setInterval(() => {
            if (remainingTime > 0 && isTimerActive) {
                setRemainingTime(remainingTime - 1);
            } else if (remainingTime === 0) {
                // Délai écoulé
                alert("Le temps imparti pour la question est écoulé !");
                setIsTimerActive(false);
            }
        }, 1000);

        return () => clearInterval(interval);
    }, [remainingTime, isTimerActive]);

    const handleStartTimer = () => {
        setIsTimerActive(true);
        questionRef.current.focus(); // Focus sur le champ de réponse
    };

    const handleSubmit = (e, questionIndex) => {
        e.preventDefault();

        const answer = e.target.answer.value;

        // Envoyer la réponse au serveur ou l'enregistrer localement

        // Mettre à jour la liste des réponses
        setAnswers([...answers, { questionIndex, answer }]);

        // Réinitialiser le chronomètre
        setRemainingTime(60);

        // Vider le champ de réponse
        e.target.answer.value = '';

        alert("Votre réponse à la question " + (questionIndex + 1) + " a été soumise !");
        setIsTimerActive(false);
    };

    return (
        <div>
            <h1>Formulaire de réponses aux questions ouvertes</h1>
            {questions.map((question, index) => (
                <div key={question.texte}>
                    <h2>{question.texte}</h2>
                    <p>{question.description}</p>
                    <p>Temps restant : {remainingTime} secondes</p>
                    <button onClick={handleStartTimer}>Démarrer le chronomètre</button>
                    <form onSubmit={(e) => handleSubmit(e, index)}>
                        <label htmlFor="answer">Votre réponse : </label>
                        <textarea id="answer" name="answer" ref={questionRef} />
                        <button type="submit">Valider</button>
                    </form>
                </div>
            ))}
        </div>
    );
};

export default QuestionPage;
