import React, { useState } from 'react';

import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

const RulesModal = () => {
    const [show, setShow] = useState(true);

    const handleClose = () => setShow(false);

    return (
        <Modal show={show} onHide={handleClose}>
            <Modal.Header style={{ backgroundColor: 'red' }} closeButton>
                <Modal.Title>Rappel des règles</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <ul>
                    <li>
                        <p>
                            <strong>Règle 1:</strong> Lisez attentivement chaque question avant de répondre.
                        </p>
                    </li>
                    <li>
                        <p>
                            <strong>Règle 2:</strong> Vous avez 2 minutes pour répondre à chaque question.
                        </p>
                    </li>
                    <li>
                        <p>
                            <strong>Règle 3:</strong> Vous ne pouvez pas utiliser de documents ou d'appareils électroniques pendant l'examen.
                        </p>
                    </li>
                </ul>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                    Fermer
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

export default RulesModal;
