import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core";

const useStyles = makeStyles({
    timer: {
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        fontSize: "24px",
        fontWeight: "bold",
        margin: "20px 0",
    },
    time: {
        marginRight: "10px",
    },
    expired: {
        color: "red",
    },
});

const Timer = ({ timeLeft }) => {
    const classes = useStyles();
    const [minutes, setMinutes] = useState(Math.floor(timeLeft / 60));
    const [seconds, setSeconds] = useState(timeLeft % 60);

    useEffect(() => {
        const interval = setInterval(() => {
            if (seconds === 0) {
                setMinutes((prevMinutes) => prevMinutes - 1);
                setSeconds(59);
            } else {
                setSeconds((prevSeconds) => prevSeconds - 1);
            }

            if (minutes === 0 && seconds === 0) {
                // Gérer le temps écoulé
                // ...
            }
        }, 1000);
        return () => clearInterval(interval);
    }, [seconds]);

    return (
        <div className={classes.timer}>
            <span className={classes.time}>{minutes}</span>
            <span>:</span>
            <span className={classes.time}>{seconds}</span>
            {minutes === 0 && seconds === 0 && <span className={classes.expired}>Temps écoulé !</span>}
        </div>
    );
};

export default Timer;
