import React, { useState, useEffect } from 'react';

const QuestionReponseChrono = ({ question, tempsReponse }) => {
    const [tempsRestant, setTempsRestant] = useState(tempsReponse);
    const [reponseSaisie, setReponseSaisie] = useState('');

    useEffect(() => {
        const interval = setInterval(() => {
            if (tempsRestant > 0) {
                setTempsRestant(tempsRestant - 1);
            }
        }, 1000);

        return () => clearInterval(interval);
    }, [tempsRestant]);

    const handleReponseChange = (event) => {
        setReponseSaisie(event.target.value);
    };

    const handleReponseSubmit = () => {
        // Stocker la réponse saisie
        // Gérer la suite du questionnaire (passer à la question suivante, etc.)
    };

    return (
        <div className="question-reponse-chrono">
            <h3>{question.texte}</h3>
            <div className="chrono">{tempsRestant} secondes restantes</div>
            <input type="text" value={reponseSaisie} onChange={handleReponseChange} />
            <button onClick={handleReponseSubmit}>Valider</button>
        </div>
    );
};

export default QuestionReponseChrono;
